import "./App.css";
import styled from "styled-components";

const StyleHeader = styled.div`
  .header {
    /* banner */
    &-banner {
      width: 100%;
      height: 3.2rem;
      background-color: ${(props) => props.theme.primaryColor};
      display: flex;
      justify-content: space-between;
      align-items: center;
      &__left-text {
        font-size: 1.2rem;
        font-weight: 400;
        color: ${(props) => props.theme.whiteColor};
        margin-left: 1rem;
      }
      &__left-icon {
        margin-right: 1rem;
      }
      &__right {
        .menu-banner__right {
          display: flex;
          gap: 0 2rem;
          align-items: center;
          font-size: 1.4rem;
          list-style: none;
          text-decoration: none;
          margin-right: 2rem;
        }
        .menu-banner__link {
          color: ${(props) => props.theme.whiteColor};
          text-decoration: none;
        }
      }
    }
    /* content */
    &-content {
      margin-top: 1.5rem;
      &__logo {
        width: 20rem;
        height: 4rem;
      }
      &__main {
        display: flex;
        align-items: center;
        gap: 0 1rem;
      }
      &__center {
        width: 100%;
        max-width: 677px;
        position: relative;
      }
      &__input {
        width: 100%;
        padding: 1.5rem 12rem 1.5rem 3rem;
        border-radius: 2rem;
        border: none;
        border: 1px solid ${(props) => props.theme.grayColor};
        outline: none;
        font-size: 1.5rem;
      }
      &__button {
        position: absolute;
        top: 0;
        right: 0;
        padding: 1.7rem 4rem;
        background-color: ${(props) => props.theme.primaryColor};
        border-radius: 4rem;
        font-size: 1.5rem;
        color: ${(props) => props.theme.whiteColor};
      }
      &__tag {
        display: inline-block;
        margin-top: 1rem;
        font-size: 1.3rem;
        font-weight: 700;
        color: ${(props) => props.theme.textPrimary};
