import React from "react";
import styled from "styled-components";
import Button from "../../components/button/Button";
import NewItems from "../../components/product/NewItems";

const StyleNews = styled.div`
  margin-top: 5rem;
  .new-main {
    display: flex;
  }
  .new-left {
    width: 60%;
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-gap: 24px 12px;
    border-radius: 5px;
    background-color: #fff;
    padding: 12px;
    box-shadow: 0 1px 3px -1px rgb(200 200 200 / 10%),
      0 1px 4px 1px rgb(200 200 200 / 5%), 0 1px 6px 2px rgb(200 200 200 / 5%);
  }
  .new-right {
    width: 40%;
  }
`;

const News = () => {
  return (
    <StyleNews>
      <div className="container">
        <div className="new-main">
          <div className="new-left">
              <NewItems></NewItems>
              <NewItems></NewItems>
              <NewItems></NewItems>
              <NewItems></NewItems>
              <NewItems></NewItems>
              <NewItems></NewItems>
              <NewItems></NewItems>
              <NewItems></NewItems>
          </div>
          <div className="new-right">
          <Button>Câu hỏi thường gặp</Button>
            <div className="new-right__list">
              <div className="new-right__items">
              <img src="" alt="" />
                <p className="new-right__text">Khi nào tôi nhận được hàng ?</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </StyleNews>
  );
};

export default News;
