import React from "react";
import styled from "styled-components";
import Button from "../../components/button/Button";
import ButtonTransparent from "../../components/button/ButtonTransparent";
import Product from "../../components/product/Product";
import { SwiperSlide, Swiper } from "swiper/react";
import "swiper/scss";
const StyleSellingProduct = styled.div`
  margin-top: 5rem;
  .selling-product__main {
    display: flex;
    gap: 0 1rem;
  }
  .selling-product__heading {
    margin-bottom: 1rem;
    display: flex;
    align-items: center;
    justify-content: space-between;
  }
  .heading-left {
    display: flex;
    gap: 0 1rem;
  }
`;

const SellingProduct = () => {
  return (
    <StyleSellingProduct>
      <div className="container">
        <div className="selling-product__heading">
          <div className="heading-left">
            <Button>Sản phẩm bán chạy</Button>
            <ButtonTransparent>Mới nhất</ButtonTransparent>
            <ButtonTransparent>Giá tốt</ButtonTransparent>
          </div>
          <div className="heading-right"></div>
          <Button>Sản phẩm bán chạy</Button>
        </div>
        <div className="selling-product__main">
          <Swiper grabCursor={"true"} spaceBetween={40} slidesPerView={"auto"}>
            <SwiperSlide>
              <Product></Product>
            </SwiperSlide>
            <SwiperSlide>
              <Product></Product>
            </SwiperSlide>
            <SwiperSlide>
              <Product></Product>
            </SwiperSlide>
            <SwiperSlide>
              <Product></Product>
            </SwiperSlide>
            <SwiperSlide>
              <Product></Product>
            </SwiperSlide>
            <SwiperSlide>
              <Product></Product>
            </SwiperSlide>
            <SwiperSlide>
              <Product></Product>
            </SwiperSlide>
          </Swiper>
        </div>
      </div>
    </StyleSellingProduct>
  );
};

export default SellingProduct;
