import React from "react";
import styled from "styled-components";
const StyleWrapperMenu = styled.div`
  .wrapper-menu {
    margin-top: 1rem;
    display: flex;
    gap: 0 1rem;
  }
  .menu {
    width: 20%;
    box-shadow: 0 0 20px 10px ${(props) => props.theme.grayColor};
  }
  .menu-list {
    position: relative;
  }
  .menu-items {
    display: flex;
    align-items: center;
    gap: 0 1rem;
    padding: 1rem;
    &__text {
      font-size: 1.4rem;
      font-weight: 500;
    }
    &__icon {
      font-size: 1.6rem;
    }
    &__dropdown {
      transform: rotate(270deg);
      font-size: 1.6rem;
      margin-left: auto;
    }
    &:hover {
      background-color: ${(props) => props.theme.primaryColor};
      color: ${(props) => props.theme.whiteColor};
      cursor: pointer;
    }
  }
  .banner {
    width: 80%;
  }
  .banner-list {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    grid-template-rows: 220px 220px 100px;
    gap: 1rem;
  }
  .banner-large {
    grid-column: 1/4;
  }
  .banner-small {
    grid-column: 4/5;
  }
  .banner-small:last-child {
    grid-column: 4/-3;
  }
  .banner-long {
    grid-row: 3/4;
    grid-column: -5/-1;
  }
  .menu-dropdown {
    position: absolute;
    top: 0;
    right: 0;
    transform: translate(100%, 0);
    &__items {
    }
    &__text {
      padding: 1rem;
      font-size: 1.4rem;
      font-weight: 500;
    }
  }
`;

const Banner = () => {
  return (
    <StyleWrapperMenu>
      <div className="container">
        <div className="wrapper-menu">
          <div className="menu">
            <div className="menu-list">
              <div className="menu-items">
                <i class="fa-solid fa-notes-medical menu-items__icon"></i>
                <p className="menu-items__text">Thực phẩm chức năng</p>
                <i class="fa-solid fa-circle-chevron-down menu-items__dropdown"></i>
              </div>
              <div className="menu-dropdown">
                <div className="menu-dropdown__items">
                  <p className="menu-dropdown__text">demo</p>
                  <p className="menu-dropdown__text">demo</p>
                  <p className="menu-dropdown__text">demo</p>
                  <p className="menu-dropdown__text">demo</p>
                  <p className="menu-dropdown__text">demo</p>
                  <p className="menu-dropdown__text">demo</p>
                  <p className="menu-dropdown__text">demo</p>
                  <p className="menu-dropdown__text">demo</p>
                  <p className="menu-dropdown__text">demo</p>
                </div>
              </div>
              <div className="menu-items">
                <i class="fa-solid fa-wand-magic-sparkles menu-items__icon"></i>
                <p className="menu-items__text">Mỹ phẩm</p>
                <i class="fa-solid fa-circle-chevron-down menu-items__dropdown"></i>
              </div>
              <div className="menu-items">
                <i class="fa-solid fa-person-breastfeeding menu-items__icon"></i>
                <p className="menu-items__text">Mẹ và Bé</p>
                <i class="fa-solid fa-circle-chevron-down menu-items__dropdown"></i>
              </div>
              <div className="menu-items">
