import "./App.css";
import styled, { css } from "styled-components";

const StyleHeader = styled.div`
  .header {
    /* banner */
    &-banner {
      width: 100%;
      height: 3.2rem;
      background-color: ${(props) => props.theme.primaryColor};
      display: flex;
      justify-content: space-between;
      align-items: center;
      &__left-text {
        font-size: 1.2rem;
        font-weight: 400;
        color: ${(props) => props.theme.whiteColor};
        margin-left: 1rem;
      }
      &__left-icon {
        margin-right: 1rem;
      }
      &__right {
        .menu-banner__right {
          display: flex;
          gap: 0 2rem;
          align-items: center;
          font-size: 1.4rem;
          list-style: none;
          text-decoration: none;
          margin-right: 2rem;
        }
        .menu-banner__link {
          color: ${(props) => props.theme.whiteColor};
          text-decoration: none;
        }
      }
    }
    /* content */
    &-content {
      margin-top: 1.5rem;
      &__logo {
        width: 20rem;
        height: 4rem;
      }
      &__main {
        display: flex;
        align-items: center;
        gap: 0 1rem;
      }
      &__center {
        width: 100%;
        max-width: 677px;
        position: relative;
      }
      &__input {
        width: 100%;
        padding: 1.5rem 12rem 1.5rem 3rem;
        border-radius: 2rem;
        border: none;
        border: 1px solid ${(props) => props.theme.grayColor};
        outline: none;
        font-size: 1.5rem;
      }
      &__button {
        position: absolute;
        top: 0;
        right: 0;
        padding: 1.7rem 4rem;
        background-color: ${(props) => props.theme.primaryColor};
        border-radius: 4rem;
        font-size: 1.5rem;
        color: ${(props) => props.theme.whiteColor};
      }
      &__tag {
        display: inline-block;
        margin-top: 1rem;
        font-size: 1.3rem;
        font-weight: 700;
        color: ${(props) => props.theme.textPrimary};
        cursor: pointer;
      }
      &__list {
        display: flex;
        align-items: center;
        gap: 0 0.5rem;
      }
      .header-items__img {
        width: 5rem;
        height: 4rem;
        cursor: pointer;
      }
      .header-content__items {
        display: flex;
        align-items: center;
      }
      .header-items__content {
        margin-right: 3rem;
      }
      .header-items__text {
        font-size: 1.2rem;
        font-weight: 700;
      }
      .header-items__number {
        font-size: 1.5rem;
        font-weight: 700;
        color: ${(props) => props.theme.redColor};
      }
    }
  }
`;
const StyleMainBar = styled.div`
  margin-top: 1rem;
  background-color: ${(props) => props.theme.grayColor};
  .main-bar {
    &__list {
      display: flex;
      justify-content: center;
    }
    &__items {
      padding: 1.4rem 2rem;
      display: flex;
      align-items: center;
      gap: 0 1rem;
    }
    &__title {
      font-size: 2rem;
      font-weight: 700;
    }
    &__text {
      font-size: 1.1rem;
      font-weight: 400;
    }
    &__img {
      width: 4rem;
      height: 4rem;
      background-color: #00aefd;
      border-radius: 100rem;
      display: flex;
      align-items: center;
      justify-content: center;
    }
    &__icon {
      font-size: 1.6rem;
      color: ${(props) => props.theme.whiteColor};
    }
  }
`;
const StyleWrapperMenu = styled.div`
  .wrapper-menu {
    margin-top: 1rem;
    display: flex;
    gap: 0 1rem;
  }
  .menu {
    width: 20%;

    box-shadow: 0 0 20px 10px ${(props) => props.theme.grayColor};
  }
  .menu-items {
    display: flex;
    align-items: center;
    gap: 0 1rem;
    padding: 1rem;
    &__text {
      font-size: 1.4rem;
      font-weight: 500;
    }
    &__icon {
      font-size: 1.6rem;
    }
    &__dropdown {
      transform: rotate(270deg);
      font-size: 1.6rem;
    }
  }
  .banner {
    width: 80%;
  }
  .banner-list {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    grid-template-rows: 220px 220px 100px;
    gap: 1rem;
  }
  .banner-large {
    grid-column: 1/4;
  }
  .banner-small {
    grid-column: 4/5;
  }
  .banner-small:last-child {
    grid-column: 4/-3;
  }
  .banner-long {
    grid-row: 3/4;
    grid-column: -5/-1;
  }
`;
function App() {
  return (
    <>
      <StyleHeader className="header">
        <div className="header-banner">
          <div className="header-banner__left">
            <p className="header-banner__left-text">
              <i class="fa-solid fa-circle header-banner__left-icon"></i>
              Kệ đựng đũa, dao kéo Hobby Home Decor KDMT3 có khay hứng...: Cao
              Thị Hằng bình luận "Sản phẩm chắc đẹp, mà mấy cái hộp đựng đũa
              hơi..."
            </p>
          </div>
          <div className="header-banner__right">
            <ul className="menu-banner__right">
              <li className="menu-banner__items">
                <a href="/#" className="menu-banner__link">
                  Tải ứng dụng Chiaki
                </a>
              </li>
              <li className="menu-banner__items">
                <a
                  href="/#"
                  className="menu-banner__link"
                  style={{ color: "#FFF5B6" }}
                >
                  Khiếu nại, báo lỗi: 0964.898.762
                </a>
              </li>
              <li className="menu-banner__items">
                <a href="/#" className="menu-banner__link">
                  Tin tức
                </a>
              </li>
              <li className="menu-banner__items">
                <a href="/#" className="menu-banner__link">
                  Tra cứu đơn hàng
                </a>
              </li>
            </ul>
          </div>
        </div>
        <div className="header-content">
          <div className="container">
            <div className="header-content__main">
              <img
                src="/logo-menu.png"
                alt=""
                className="header-content__logo"
              />
              <div className="header-content__center">
                <div className="header-content__form">
                  <input
                    type="text"
                    name=""
                    id=""
                    className="header-content__input"
                    placeholder="Tìm kiếm hàng nghìn Sản Phẩm, Thương hiệu..."
                  />
                </div>
                <a href="/#">
                  <i class="fa-solid fa-magnifying-glass header-content__button"></i>
                </a>
                <p className="header-content__tag">
                  Son Môi, Healthy Care, Blackmores, Obagi, Collagen
                </p>
              </div>
              <div className="header-content__actions">
                <div className="header-content__list">
                  <div className="header-content__items">
                    <img
                      src="/account.svg"
                      className="header-items__img"
                      alt=""
                    />
                  </div>
                  <div className="header-content__items">
                    <img src="/faq.svg" alt="" className="header-items__img" />
                  </div>
                  <div className="header-content__items">
                    <img
                      src="/phone.svg"
                      alt=""
                      className="header-items__img"
                    />
                    <div className="header-items__content">
                      <p className="header-items__text">
                        Hotline:
                        <span className="header-items__number">
                          0982.770.265
                        </span>
                      </p>
                      <p className="header-items__text">
                        Tổng đài:
                        <span className="header-items__number">1900 2054</span>
                      </p>
                    </div>
