import React from "react";
import styled from "styled-components";
import Button from "../../components/button/Button";
import ButtonTransparent from "../../components/button/ButtonTransparent";
import Product from "../../components/product/Product";
import { Navigation, Pagination, Scrollbar, A11y } from "swiper";
import { SwiperSlide, Swiper } from "swiper/react";
import "swiper/scss";
// Import Swiper styles
import "swiper/scss";
import "swiper/scss/navigation";
import "swiper/scss/pagination";
const StyleSellingProduct = styled.div`
  margin-top: 5rem;
  .selling-product__main {
    display: flex;
    gap: 0 1rem;
  }
  .selling-product__heading {
    margin-bottom: 1rem;
    display: flex;
    align-items: center;
    justify-content: space-between;
  }
  .heading-left {
    display: flex;
    gap: 0 1rem;
  }
`;

// response 
var swiper = new Swiper('.selling-product__main', {
  // Default parameters
  slidesPerView: 1,
  spaceBetween: 10,
  // Responsive breakpoints
  breakpoints: {
    // when window width is >= 320px
    320: {
      slidesPerView: 2,
      spaceBetween: 20
    },
    // when window width is >= 480px
    480: {
      slidesPerView: 3,
      spaceBetween: 30
    },
    // when window width is >= 640px
    1024: {
      slidesPerView: 3,
      spaceBetween: 40
    }
  }
})
const SellingProduct = () => {
  return (
    <StyleSellingProduct>
      <div className="container">
        <div className="selling-product__heading">
          <div className="heading-left">
            <Button>Sản phẩm bán chạy</Button>
            <ButtonTransparent>Mới nhất</ButtonTransparent>
            <ButtonTransparent>Giá tốt</ButtonTransparent>
          </div>
          <div className="heading-right"></div>
          <Button>Xem tất cả bán chạy</Button>
        </div>
        <div className="selling-product__main">
          <Swiper 
            modules={[Navigation, Pagination, Scrollbar, A11y]}
            spaceBetween={50}
            slidesPerView={4}
            navigation
          >
            <SwiperSlide>
              <Product></Product>
            </SwiperSlide>
            <SwiperSlide>
              <Product></Product>
            </SwiperSlide>
            <SwiperSlide>
              <Product></Product>
            </SwiperSlide>
            <SwiperSlide>
              <Product></Product>
            </SwiperSlide>
            <SwiperSlide>
              <Product></Product>
            </SwiperSlide>
            <SwiperSlide>
              <Product></Product>
            </SwiperSlide>
            <SwiperSlide>
              <Product></Product>
            </SwiperSlide>
          </Swiper>
        </div>
      </div>
    </StyleSellingProduct>
  );
};

export default SellingProduct;
