import React from "react";
import styled from "styled-components";
const StyleTrendProduct = styled.div`
margin-top: 5rem;
    .trend-header {
        &__list {
            display: flex;
            justify-content: space-between;
        }
        &__items {
            display: flex;
            align-items: center;
            padding: 1rem;
            gap: 0 1rem;
            border-radius: 1rem;
            box-shadow: 0 0 20px 6px ${props => props.theme.grayColor};;
        }
        &__title {
                font-size: 1.7rem;
                font-weight: 400;
        }
        &__text {
            font-size: 1.3rem;
            font-weight: 400;
        }
        &__img {
            max-width: 70px;
            height: 70px;
        }
    }
`

const TrendProduct = () => {
  return (
    <StyleTrendProduct>
      <div className="container">
        <div className="trend-header">
          <p className="trend-header__left">Xu hướng mua sắm</p>
          <p className="trend-header__right">Xem thêm</p>
        </div>
        <div className="trend-header__list">
            <div className="trend-header__items">
                <div className="trend-header__content">
                    <p className="trend-header__title">Toner</p>
                    <p className="trend-header__text">150+ sản phẩm</p>
                </div>
                <img src="/trendProduct-1.png" alt="" className="trend-header__img" />
            </div>
            <div className="trend-header__items">
                <div className="trend-header__content">
                    <p className="trend-header__title">Kem chống nắng</p>
                    <p className="trend-header__text">250+ sản phẩm</p>
                </div>
                <img src="/trendProduct-2.png" alt="" className="trend-header__img" />
            </div>
            <div className="trend-header__items">
                <div className="trend-header__content">
                    <p className="trend-header__title">Obagi</p>
                    <p className="trend-header__text">1k+ sản phẩm</p>
                </div>
                <img src="/trendProduct-3.png" alt="" className="trend-header__img" />
            </div>
            <div className="trend-header__items">
                <div className="trend-header__content">
                    <p className="trend-header__title">Obagi</p>
                    <p className="trend-header__text">1k+ sản phẩm</p>
                </div>
                <img src="/trendProduct-4.png" alt="" className="trend-header__img" />
            </div>
            <div className="trend-header__items">
                <div className="trend-header__content">
                    <p className="trend-header__title">Obagi</p>
                    <p className="trend-header__text">1k+ sản phẩm</p>
                </div>
                <img src="/trendProduct-5.png" alt="" className="trend-header__img" />
            </div>
            <div className="trend-header__items">
                <div className="trend-header__content">
                    <p className="trend-header__title">Obagi</p>
                    <p className="trend-header__text">1k+ sản phẩm</p>
                </div>
                <img src="/trendProduct-6.png" alt="" className="trend-header__img" />
            </div>
        </div>
      </div>
    </StyleTrendProduct>
  );
};

export default TrendProduct;
